package com.whw.footstones.core.error;import com.whw.footstones.common.exception.ExceptionEnums;import lombok.Getter;@Getterpublic enum DefaultSystemError implements ExceptionEnums {    /**     * 请求部正确     */    THIRD_SERVICE_ERROR(4, "调用第三方接口出错了"),    /**     * 请求部正确     */    BAB_REQUEST(3, "请检查请求method等"),    /**     * 服务异常     */    SERVER_BUSY(2, "系统繁忙，请稍后！"),    /**     * 请求参数错误     */    BAD_PARAM(1, "请求参数错误！");    public static final int MODULE = 100;    private int code;    private String message;    DefaultSystemError(int value, String message){        this.code = MODULE * 100 + value;        this.message = message;    }}