package com.whw.footstones.core.message;/** * @version 1.0 * @description: TODO * @date 2021/9/13 7:33 PM */public class InputMessage<T> {    /**     * 请求body     */    public T params;    public InputMessage(T params) {        this.params = params;    }    public InputMessage() {    }    public T getParams() {        return params;    }    public void setParams(T params) {        this.params = params;    }    /**     * 构建请求体     *     * @param params     * @return     */    public static InputMessage params(Object params) {        return new InputMessage(params);    }}