package com.whw.footstones.core.page;import io.swagger.annotations.ApiModelProperty;import javax.validation.constraints.NotNull;import java.util.List;/** * @version 1.0 * @description: TODO * @date 2021/9/24 3:08 PM */public class PageResponse<T> {    @NotNull    @ApiModelProperty("页码")    private Integer page;    @NotNull    @ApiModelProperty("单页大小")    private Integer pageSize;    @NotNull    @ApiModelProperty("总条数")    private Long totalCount;    @NotNull    @ApiModelProperty("总页数")    private Integer pageCount;    @ApiModelProperty("内容")    private List<T> itemList;    public PageResponse() {        super();    }    public PageResponse(int totalCount, List<T> itemList, PageRequest page) {        this((long) totalCount, itemList, page);    }    public PageResponse(long totalCount, List<T> itemList, PageRequest page) {        this.page = page.getPage();        this.pageSize = page.getPageSize();        this.totalCount = totalCount;        this.pageCount = Long.valueOf (page.getPageSize() <= 0 ? 0 : (totalCount                % page.getPageSize() == 0) ? totalCount / page.getPageSize()                : totalCount / page.getPageSize() + 1).intValue();        this.itemList = itemList;    }    public Integer getPage() {        return page;    }    public void setPage(Integer page) {        this.page = page;    }    public Integer getPageSize() {        return pageSize;    }    public void setPageSize(Integer pageSize) {        this.pageSize = pageSize;    }    public Long getTotalCount() {        return totalCount;    }    public void setTotalCount(Long totalCount) {        this.totalCount = totalCount;    }    public Integer getPageCount() {        return pageCount;    }    public void setPageCount(Integer pageCount) {        this.pageCount = pageCount;    }    public List<T> getItemList() {        return itemList;    }    public void setItemList(List<T> itemList) {        this.itemList = itemList;    }    @Override    public String toString() {        return "PageResponse [page=" + page + ", pageSize=" + pageSize + ", totalCount=" + totalCount + ", pageCount=" + pageCount + ", itemList=" + itemList                + "]";    }}