package com.whw.footstones.core.page;import com.github.pagehelper.PageInfo;import io.swagger.annotations.ApiModelProperty;import lombok.Data;import javax.validation.constraints.NotNull;import java.util.List;/** * @author chenlinya * @version 1.0 * @description: TODO * @date 2021/9/24 3:08 PM */@Datapublic class PageResponse<T> {    @NotNull    @ApiModelProperty("页码")    private Integer page;    @NotNull    @ApiModelProperty("单页大小")    private Integer pageSize;    @NotNull    @ApiModelProperty("总条数")    private Long totalCount;    @NotNull    @ApiModelProperty("总页数")    private Integer pageCount;    @ApiModelProperty("内容")    private List<T> itemList;    public PageResponse(PageInfo<T> pageInfo) {        this.page = pageInfo.getPageNum();        this.pageSize = pageInfo.getPageSize();        this.totalCount = pageInfo.getTotal();        this.pageCount = pageInfo.getPages();        this.itemList = pageInfo.getList();    }    @Override    public String toString() {        return "PageResponse [page=" + page + ", pageSize=" + pageSize + ", totalCount=" + totalCount + ", pageCount=" + pageCount + ", itemList=" + itemList                + "]";    }}