package com.whw.basic.config;import org.redisson.Redisson;import org.redisson.api.RLock;import org.redisson.api.RedissonClient;import org.redisson.config.Config;import org.springframework.context.annotation.Configuration;/** * @version 1.0 * @description: TODO * @date 2021/9/3 8:55 AM */@Configurationpublic class RedisConfig {    public static void main(String[] args) {        Config config = new Config();        config.useSingleServer().setAddress("redis://127.0.0.1:6379");        config.useSingleServer().setPassword("redis1234");        final RedissonClient client = Redisson.create(config);        RLock lock = client.getLock("lock1");        try {            lock.lock();        } finally {            lock.unlock();        }    }}