package com.whw.footstones.config;import lombok.extern.slf4j.Slf4j;import org.slf4j.MDC;import javax.servlet.*;import javax.servlet.annotation.WebFilter;import java.io.IOException;import java.util.UUID;/** * @author cly * @version 1.0 * @description: TODO * @date 6/14/24 7:36 PM */@Slf4j@WebFilter(filterName = "traceIdFilter", urlPatterns = "/*")public class TraceIdFilter implements Filter {    /**     * 日志跟踪标识     */    private static final String TRACE_ID = "session_id";    @Override    public void init(FilterConfig filterConfig) throws ServletException {    }    @Override    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain)            throws IOException, ServletException {        MDC.put(TRACE_ID, UUID.randomUUID().toString());        filterChain.doFilter(servletRequest, servletResponse);    }    @Override    public void destroy() {        MDC.clear();    }}