package com.whw.footstones.config;import com.whw.footstones.log.LogFilter;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import org.springframework.web.servlet.mvc.method.annotation.RequestMappingHandlerAdapter;import java.util.List;/** * @author cly * @version 1.0 * @description: TODO * @date 10/24/23 2:02 PM */@Configurationpublic class WebConfig implements WebMvcConfigurer {    @Bean(name = "myRequestMappingHandlerAdapter")    public RequestMappingHandlerAdapter requestMappingHandlerAdapter() {        return new RequestMappingHandlerAdapter();    }    @Value("${micro.logging.api.ignore:/*/actuator/health}")    private List<String> ignoresUri;    @Bean    public FilterRegistrationBean logFilterRegistrationBean() {        FilterRegistrationBean registration = new FilterRegistrationBean();        registration.setFilter(new LogFilter(ignoresUri));        registration.addUrlPatterns("/*");        registration.setName("logFilter");        registration.setOrder(1);        return registration;    }}