package com.whw.footstones.listener;import org.springframework.context.event.EventListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;/** * @author cly * @version 1.0 * @description: TODO * @date 2021/12/1 2:42 PM */@Componentpublic class MyListener {    @EventListener    @Async    public void listener1(MyListenerEvent event) {        System.out.println("注解监听器1:" + event.getMsg());    }}