package com.whw.footstones.listener;import org.springframework.context.ApplicationEvent;/** * @author cly * @version 1.0 * @description: TODO * @date 2021/12/1 2:08 PM */public class MyListenerEvent extends ApplicationEvent {    private static final long serialVersionUID = 1L;    private String msg;    public MyListenerEvent(Object source, String msg) {        super(source);        this.msg = msg;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }}