package com.whw.footstones.listener;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationContext;import org.springframework.stereotype.Component;/** * @author cly * @version 1.0 * @description: TODO * @date 2021/12/1 2:08 PM */@Componentpublic class MyListenerPlusher {    @Autowired    private ApplicationContext applicationContext;    public void pushListener(String msg) {        applicationContext.publishEvent(new MyListenerEvent(this, msg));    }}